        parent::__construct();
        $this->load->model('Menu_olah_model');
    } 

    /*
     * Listing of menu_olah
     */
    function index()
    {
        $ammount = 20;
        $sender_id = 86;
        $receiver_id = 22;
       
        $this->Menu_olah_model->transaction($ammount, $sender_id, $receiver_id);

        $data['menu_olah'] = $this->Menu_olah_model->get_all_menu_olah();
        
        $data['_view'] = 'menu_olah/index';
        $this->load->view('layouts/main',$data);
